using Cosmos.Core.Memory;
using Cosmos.System;
using Cosmos.System.Graphics;
using IL2CPU.API.Attribs;
using System;
using System.Drawing;
using System.IO;

namespace Cup.sys.gui
{
    public static class GUI
    {
        [ManifestResourceStream(ResourceName = "Cup.sys.gui.res.wp.wallpaper1.bmp")] static byte[] wp;
        // Cursors different sizes
        [ManifestResourceStream(ResourceName = "Cup.sys.gui.res.cur.aero1.bmp")] static byte[] cur1;
        [ManifestResourceStream(ResourceName = "Cup.sys.gui.res.cur.aero2.bmp")] static byte[] cur2;
        [ManifestResourceStream(ResourceName = "Cup.sys.gui.res.cur.aero3.bmp")] static byte[] cur3;
        [ManifestResourceStream(ResourceName = "Cup.sys.gui.res.cur.aero4.bmp")] static byte[] cur4;
        [ManifestResourceStream(ResourceName = "Cup.sys.gui.res.cur.aero5.bmp")] static byte[] cur5;
        static Bitmap Cursor_1px = new(cur1);
        static Bitmap Cursor_2px = new(cur2);
        static Bitmap Cursor_3px = new(cur3);
        static Bitmap Cursor_4px = new(cur4);
        static Bitmap Cursor_5px = new(cur5);
        static Bitmap Wallpaper = new(wp);

        public static SVGAIICanvas canvas;
        public static Mouse_State mouse;
        static int CursorPx = 1;
        static Button Menu = new("Menu", 8, 1030, true);
        static Button Menu_Settings = new("Settings", 5, 682, false);
        static Button Menu_Explorer = new("Explorer", 5, 718, false); // 5, 718
        static Button Menu_CBreakIDE = new("CBreak IDE", 5, 754, false);
        static Button Menu_Terminal = new("Terminal", 5, 790, false);
        static Button Menu_About = new("About", 5, 826, false);
        static Button Menu_Internet = new("Internet", 5, 863, false);
        static Button Menu_TaskManager = new("Task Manager", 5, 900, false);
        static Button Menu_Shutdown = new("Shutdown", 5, 937, false);
        static Button Menu_Reboot = new("Reboot", 5, 973, false);
        public enum Mouse_State
        {
            Ordinary,
            Waiting
        }
        public static void Enable()
        {
            canvas = (SVGAIICanvas)FullScreenCanvas.GetFullScreenCanvas(new(1920, 1080, ColorDepth.ColorDepth32));
            MouseManager.ScreenHeight = 1080;
            MouseManager.ScreenWidth = 1920;
            string CustomCharset = @"ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyzАБВГДЕЁЖЗИЙКЛМНОПРСТУФХЦЧШЩЫЪЬЭЮЯабвгдеёжзийклмнопрстуфхцчщшэюяы0123456789:;,.()!?\|/";
            MemoryStream CalibriCustomCharset16 = new MemoryStream(Convert.FromBase64String(""));
            BitFont.RegisterBitFont("Ordinary Font", new BitFontDescriptor(CustomCharset, CalibriCustomCharset16, 16));
            string CustomCharset2 = @"ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyzАБВГДЕЁЖЗИЙКЛМНОПРСТУФХЦЧШЩЫЪЬЭЮЯабвгдеёжзийклмнопрстуфхцчщшэюяы0123456789:;,.()!?\|/";
            MemoryStream ArialCustomCharset24 = new MemoryStream(Convert.FromBase64String(""));
            BitFont.RegisterBitFont("Ordinary Font 24", new BitFontDescriptor(CustomCharset2, ArialCustomCharset24, 24));
            mouse = Mouse_State.Ordinary;
        }
        public static void DrawMouse()
        {
            if (mouse == Mouse_State.Ordinary)
            {
                if (CursorPx == 1) { canvas.DrawImageAlpha(Cursor_1px, (int)MouseManager.X, (int)MouseManager.Y); }
                else if (CursorPx == 2) { canvas.DrawImageAlpha(Cursor_2px, (int)MouseManager.X, (int)MouseManager.Y); }
                else if (CursorPx == 3) { canvas.DrawImageAlpha(Cursor_3px, (int)MouseManager.X, (int)MouseManager.Y); }
                else if (CursorPx == 4) { canvas.DrawImageAlpha(Cursor_4px, (int)MouseManager.X, (int)MouseManager.Y); }
                else if (CursorPx == 5) { canvas.DrawImageAlpha(Cursor_5px, (int)MouseManager.X, (int)MouseManager.Y); }
            }
            
        }
        public static void DrawDesktop() // 8 1027
        {
            canvas.DrawImage(Wallpaper, 0, 0);
            canvas.DrawFilledRectangle(new(Color.Black), new Cosmos.System.Graphics.Point(0, 1020), 1920, 60);
            canvas.DrawRectangle(new(Color.DarkGray), new Cosmos.System.Graphics.Point(0, 1020), 1920, 59);

            BitFont.DrawBitFontString("Ordinary Font 24", Color.White, DateTime.Now.ToString(), 1700, 1038);
            Menu.Draw();
            if (Menu.WasClicked()) { DrawMenu(); }
            if (!Menu.IsMouseOver() && MouseManager.MouseState == MouseState.Left) { Menu.was_clicked = false; }
        }
        public static void DrawMenu()
        {
            canvas.DrawFilledRectangle(new(Color.Black), 0, 678, 171, 342);
            canvas.DrawRectangle(new(Color.DarkGray), 0, 678, 171, 342);

            Menu_Settings.Draw();
            Menu_Explorer.Draw();
            Menu_CBreakIDE.Draw();
            Menu_Terminal.Draw();
            Menu_About.Draw();
            Menu_Internet.Draw();
            Menu_TaskManager.Draw();
            Menu_Shutdown.Draw();
            Menu_Reboot.Draw();

            if (Menu_Shutdown.WasClicked()) { Power.Shutdown(); }
            else if (Menu_Reboot.WasClicked()) { Power.Reboot(); }

            else if (Menu_Settings.WasClicked()) { Menu.was_clicked = false; ClearAllMenuComponents(); /* Do some stuff */ }
            else if (Menu_Explorer.WasClicked()) { Menu.was_clicked = false; ClearAllMenuComponents();/* Do some stuff */ }
            else if (Menu_Terminal.WasClicked()) { Menu.was_clicked = false; ClearAllMenuComponents();/* Do some stuff */ }
            else if (Menu_About.WasClicked()) { Menu.was_clicked = false; ClearAllMenuComponents();/* Do some stuff */ }
            else if (Menu_Internet.WasClicked()) { Menu.was_clicked = false; ClearAllMenuComponents();/* Do some stuff */ }
            else if (Menu_Settings.WasClicked()) { Menu.was_clicked = false; ClearAllMenuComponents();/* Do some stuff */ }
            else if (Menu_CBreakIDE.WasClicked()) { Menu.was_clicked = false; ClearAllMenuComponents();/* Do some stuff */ }
            else if (Menu.WasClicked()) { Menu.was_clicked = false; }
            Heap.Collect();
        }
        private static void ClearAllMenuComponents()
        {
            Menu_Shutdown.was_clicked = false;
            Menu_Reboot.was_clicked = false;
            Menu_Terminal.was_clicked = false;
            Menu_Explorer.was_clicked = false;
            Menu_About.was_clicked = false;
            Menu_Internet.was_clicked = false;
            Menu_Settings.was_clicked = false;
            Menu_CBreakIDE.was_clicked = false;
        }
    }
}
